import React from 'react';

const CheckBox = ({ options, value, setValue, ...props }) => {
  function handleChange({ target }) {
    if (target.checked) {
      // verifico se aoguma opção está checada
      setValue([...value, target.value]); // passando todos os valores antigos com o spreed e depois passando o novo valor
    } else {
      // para remover o valor não checado da array caso tenha
      setValue(value.filter((itemValue) => itemValue !== target.value)); // apenas fazendo essa verificação ele irá filtar os valores (os 'itemValue') que são diferentes do 'target.value'  (que são os valores checados), estão todo 'itemValue' que é cada item/opção existente, que for diferente de um item checado (que pé o target.value), ele irá filtrar e retirar esse valores
      console.log(target.value);
    }
  }

  return (
    <div>
      {options.map((option) => (
        <label key={option}>
          <input type="checkbox" value={options} onChange={handleChange} checked={value.includes(option)} />{/*  */}
          {option}
        </label>
      ))}
    </div>
  );
};

export default CheckBox;
