import React from 'react';

const App = () => {
  // fazendo com um obj dentro dela
  const [form, setForm] = React.useState({
    nome: '',
    email: ''
  }); 

  function handleSubmit(e) {
    e.preventDefault(); // previnindo o evento padrão para que ele não atualize a página
  }

  function handleChange({target}) { // desestruturando o 'event' e pegando apenas o que irei utilizar que é o 'target
    const {id, value} = target // desestruturando o target e pegando o 'id' e o 'value'
    console.log(id, value)

    setForm({ [id]: value}) 
    // [id]: value ===> essa parte é a mesma coisa que ["email"]: kaua@gmail.com
    // caso não passe o 'id' entre [], ele 
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label htmlFor="nome">Nome</label>
        <input
          id="nome" // definindo o 'htmlFor' e "id" do 'input' iguais para master a referencia e ajudar na acessisbilidade
          name="nome"
          type="text"
          value={form.nome}
          onChange={handleChange}
        />

        <label htmlFor="email">Email</label>
        <input
          id="email" // definindo o 'htmlFor' e "id" do 'input' iguais para master a referencia e ajudar na acessisbilidade
          name="email"
          type="email"
          value={form.email}
          onChange={handleChange}
        />
        <button>Enviar</button>{' '}
        {/*  Não precisando passar o evento no button por conta que ele está dentro do form e o action já entende quando clicar no 'botão' ou em 'enter' */}
      </form>
    </>
  );
};

export default App;
