import React from 'react';

const App = () => {
  const [comentarios, setComentarios] = React.useState([]);
  const [input, setInput] = React.useState('');
  const inputElement = React.useRef(); // fará referencia ao item html, que no caso será o 'input'

  function handleClick() {  
    setComentarios((comentarios) => [...comentarios, input]);
    setInput('');
    inputElement.current.focus();
  }

  return (
    <div>
      <ul>
        {comentarios.map((comentario) => ( // passando por cada comentário
          <li key={comentario}>{comentario}</li> // exibindo os comentário e passando uma chve única
        ))}
      </ul>
      <input
        type="text"
        value={input}
        ref={inputElement} // dizendo que o ref desse input é o "imputElement"
        onChange={({ target }) => setInput(target.value)} // ao mudar um valor, ele irá passar esse valor digitado pelo usuário e setando o valor de "input", assim quando clicar no botão logo a baixo, e
      />
      <br />
      <button onClick={handleClick}>Enviar</button>
    </div>
  );
};

export default App;
