import React from 'react'
import { useState, useEffect } from 'react'

const useLocalStorage = (key, inicial) => { //passando dois valores, um que é a chave e outro o valor inicial
  const [state, setState] = useState(() => { //poderia verificar com o useEffetct se ja há essa key salva lá no localStorage, porem eu posso fazer destá maneira tambem
    const local = window.localStorage.getItem(key) // procurando a chave que passei no localStorage
    return local ? local : inicial // caso não haja o valor ele retorna o 'inicial' passado lá encima 
  });

  // fazendo um estado para toda vez que eu atualizar o meu componente ele salvar lá no meu localStorage
  useEffect(() => {
    window.localStorage.setIem(key, state)
  }, [state, key]) //tendo que passar os dois arguemntos por que se não ele dará erro
  
  return [state, setState]
}

export default useLocalStorage
