import React from 'react';
import { GlobalContext } from './GlobalContext';

const Produtos = () => {
  const global = React.useContext(GlobalContext);
  if(global.dados == null) return null
  return <div>Produto: {global.dados[0]}</div>;
};

export default Produtos;
