import React from 'react';

const App = () => {
  const [cores, setCores] = React.useState(false);

  function handleChange({target}) {
    console.log(target)
    if(target.checked){ // antes ele dava um bug que quando desmarcavamos o checkboox ele adicionava o valor a array, com essa verificação isso parou, porem ainda não removeu o item da array
      setCores([...cores, target.value]) // passando a array, depois 
    }
  }

  return (
    <>
      <form>
        <label>
          <input
            type="checkbox"
            value="azul"
            // checked={box}
            onChange={handleChange} 
          />{' '}
          Azul
        </label>
        <label>
          <input
            type="checkbox"
            value="vermehlo"
            // checked={box}
            onChange={handleChange} 
          />{' '}
          Vermelho
        </label>
      </form>
    </>
  );
};

export default App;
