import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';

const Produto = ({produto}) => { // desestruturando a useState "produto" que é um objeto
  const [dados, setDados] = useState(null)

  useEffect(() => { //executandfo apenas qunado 'produto' é alterado
    if(produto !== null) { //verifica se há algo dentro de "produto", antes de fazer o fetch
      async function fetchProduct() {
        const response = await fetch(`https://ranekapi.origamid.dev/json/api/produto/${produto}`)
        const json = await response.json()
        setDados(json)
      }
      fetchProduct() // chamando a funcção asincrona
    }
  }, [produto])

  if(dados === null) return null // se caso ele seja diferente de null eele irá retornar a linha de baixo

  return (
    <>
      <h1> {dados.nome}</h1>
      <p>R$ {dados.preco}</p>
    </>
  )
}

export default Produto
