import React from 'react';
import fotoDog from './assets/foto.jpg'; // podendo passar qualquer nome depois do 'import'
import './App.css';
import dogSvg from './assets/dog.svg';
import { ReactComponent as Dog } from './assets/dog.svg'; // importando o código do SVG direto no meu html para que eu possa ter mais controle sobre ele. O porem é que se eu quiser passar outro svg eu não consigo por conta do  nome, então por isso eu utilizo 'as' e um nome próprio para o svg , assim mudando o nome da importação
import DogSvg from './DogSvg';
import { useState } from 'react';

const App = () => {
  const [eye, setEye] = useState(3);

  function aumentarEye() {
    setEye(eye + 1)
  }
  function diminuirEye() {
    setEye(eye - 1)
  }



  return (
    <div>
      <img
        style={{ height: '50px', width: '60px' }}
        src={fotoDog}
        alt="foto de cachorro"
      />{' '}
      {/* Fazendo o import da imagem invés passar direto o camingo dela em 'src', por que na hora do build final ele irá garantir que o caminho será o correto */}
      <br />
      <br />
      {/* Exibindo uma imabgem via Css passando apenas a */}
      <p className="fundo"></p>
      <br />
      <br />
      {/* Exibindo um SVG via Css passando apenas a */}
      <img src={dogSvg} alt="svgDog" />
      <br />
      <br />
      {/* Importação via ReactComponent */}
      <Dog />
      <br />
      <br />
      {/* Importação de um Svg via Javascript para que tenhamos mais ainda controle sobre ele */}
      {/* Passsando as prorpiedades que serão acessadas no arquivo 'DogSvg.js' */}
      <DogSvg color="red" heightEye="5px" />
      <br />
      <br />
      {/* Podendo tambem utilizar variaveis 'Reativas' (useState) para modificar */}
      <DogSvg color="red" heightEye={eye} />
      <button style={{marginRigth: '5'}} onClick={aumentarEye}>Aumentar olho</button>
      <button onClick={diminuirEye}>Aumentar olho</button>
    </div>
  );
};

export default App;
