import Input from './Form/Input';

export const App = () => {
  const [cep, setCep] = React.useState('');
  const [error, setError] = React.useState(null);

  function validateCep(value) {
    // recebendo com oparemetro o valor digitado pelo usuário
    console.log(value);

  }

  // validção ao usuário desfocar do campo
  function handleBlur({ target }) {
    validateCep(target.value);
  }



  // fazendo uma validação ao enviar o formulário
  function handleSubmit(event){
    event.preventDefault()
    if(validateCep(cep)){ // tendo que passar o valor digitado pelo usuário
      alert('enviou')
    }else {
      alert('Não enviou')
    }
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <Input
          type="text"
          label="CEP"
          id="cep"
          placeholder="00000-000"
          value={cep}
          onChange={hancleChange}
          setValue={setCep}
          onBlur={handleBlur} // sendo o unBlur um evento que sem,rpe será ativado quando ele for focado e desfocado, quando ele desfoca ele executa
        />
        {error && <p>{error}</p>}
        <button>Enviar</button>
      </form>
    </>
  );
};

export default App;
