const Input = (props) => { // desestruturando a label, querendo então passar o nome dela
  return (
    <div syyle={{ margin:'1rem 0' }}>
      <label htmlFor="{props.id}">{props.label}</label>
      <input id="{props.id}" type="text"/>
    </div>
  )
}

export default Input
