import React from 'react';
import useFetch from './useFetch';
import useLocalStorage from './useLocalStorage';

const App = () => {
  const [produto, setProduto] = useLocalStorage('produto', '');
  const { request, data } = useFetch(); // como eu estou retornando um objeto lá, eu posso desestruturar ele aqui

  React.useEffect(() => {
    request('https://ranekapi.origamid.dev/json/api/produto/');
  }, []);
  // console.log(data) // me retornando a array de objetos contendo os produtos

  function handleClick({target}){ // desestruturando o 'event'
    return setProduto(target.innerText)
  }

  return (
    <div>
      <p>Produto preferido: {produto}</p>
      <button style={{marginRight: '1rem'}} onClick={handleClick}>notebook</button>
      <button onClick={handleClick}>smartphone</button>

      {data.map((produto) => <div>{produto}</div>)}
    </div>
  );
};

export default App;
