import React from 'react';
import Head from './Head';
import styles from './Produtos.module.css';

const Produtos = () => {
  const [produtos, setProdutos] = React.useState(null);

  React.useEffect(() => {
    const fetchProduto = async function () {
      const response = await fetch(
        'https://ranekapi.origamid.dev/json/api/produto',
      );
      const json = await response.json();
      await setProdutos(json);
      // console.log(produtos)
    };
    fetchProduto();
  }, []); // passando uma array vazia por que quero que ele faça apenas o fetch inical

  if (produtos === null) return null;
  return (
    <>
      <Head title="Produtos" description="Página de produtos" />
      <div className={styles.produtos}>{produtos}</div>
    </>
  );
};

export default Produtos;
