import React from 'react';

const formFields = [
  {
    id: 'nome',
    label: 'Nome',
    type: 'text',
  },
  {
    id: 'email',
    label: 'Email',
    type: 'email',
  },
  {
    id: 'senha',
    label: 'Senha',
    type: 'password',
  },
  {
    id: 'cep',
    label: 'CEP',
    type: 'text',
  },
  {
    id: 'rua',
    label: 'Rua',
    type: 'text',
  },
  {
    id: 'numero',
    label: 'Numero',
    type: 'text',
  },
  {
    id: 'bairro',
    label: 'Bairro',
    type: 'text',
  },
  {
    id: 'cidade',
    label: 'Cidade',
    type: 'text',
  },
  {
    id: 'estado',
    label: 'Estado',
    type: 'text',
  },
];

const App = () => {
  // nome, email, senha, cep, rua, numero, bairro, cidade e estado
  const [form, setForm] = React.useState({

  });
  const [status, setStatus] = React.useState(null)

  function handleSubmit(e) {
    e.preventDefault(); // previnindo o evento padrão para que ele não atualize a página
    fetch('https://ranekapi.origamid.dev/json/api/usuario', { // quando eu quero fazer um post eu tenho que passar as opções, sendo elas em forma de objeto
      method: 'POST', // sendo o metodo 'get' padrão,
      headers: { // todos os cabeçalhos que quiser passar
        // para essa api o principal header que precisamos é o content 
        // sendo uma aplicação do tipo json
        'Content-Type': 'application/json', // por ser duas palavras separados por um '-', tem que se usar aspas simples 
      },
      // sendo o body o corpo desse meu post
      // porem ele só aceita string, e o nosso 'form' é uma array de objetos
      body: JSON.stringify(form) // tendo que fazer essa conversão utiliozando o 'JSON.stringfy()'
    }).then( response => {
      console.log(response)
      setStatus(response)
    })
    // sendo uma API que requer todos os dados para que dê certo e aceitará apenas emails distintos

  }

  function handleChange({ target }) {
    // desestruturando o 'event' e pegando apenas o que irei utilizar que é o 'target
    const { id, value } = target; // desestruturando o target e pegando o 'id' e o 'value'
    console.log(id, value);

    setForm({ ...form, [id]: value }); // passanod priemiro os valores antigos para ele não passar por cima
    // [id]: value ===> essa parte é a mesma coisa que ["email"]: kaua@gmail.com
    // caso não passe o 'id' entre [], ele não irá torar um itme reativo, ai só com os colchetes ele tranforma a variavel "id" como propriedade
  }

  return (
    <>
      <form onSubmit={handleSubmit}>


        {formFields.map(({ id, label, type }) => (
          <div key={id}> {/* Sempre que retorna uma lista de itens tem que definir uma KEY */}
            <label htmlFor={id}>{label}</label>
            <input
              type={type}
              id={id}
              value={form[id]} // sendo a mesma coisa que form["nome"]
              onChange={handleChange}
            />
          </div>
        ))}
        {status && status.ok && <p>Formulário enviado com sucesso!</p> } {/* Primeiro ele verifica se o status é positivo, depois verifica se o status de 'status' é true */}
        <button>Enviar</button>
      </form>
    </>
  );
};

export default App;

