import React from 'react';

const Produto = ({ nome, propriedades }) => {
  return (
    <div dtyle={{ border: '1px solid', margin: '1rem 0', padding: '1rem' }}>
      <p>{nome}</p>
      <ul>
        {propriedades.map((item) => ( // fazendo um map em propriedades para passar pela
          <li key={item}>{item}</li> // não esquecendo de passar a key
        ))}
      </ul>
    </div>
  );
};

export default Produto;
