import React, {useEffect} from 'react'

const Produto = () => {
  useEffect(() => {
    function handleScroll(e) {
      console.log(e)
    }
    window.addEventListener('scroll', handleScroll) // a priemira coisa a ser executado quando se henderiza a página
    return () => { // ao final do evento qle irá retornar o que estiver dentro do escopo
      window.removeEventListener('scroll', handleScroll) //assim removendo o evento ao final da animação
    }
  }, []) // passando uma arra\y de dependencias vazia para que ele sejá executado apenas uma vez

  return (
    <div style={{ height: '200vh'}}>
      <h1>Lista de produtos</h1>
    </div>
  )
}

export default Produto
