import React from 'react';

const Radio = ({ options, value, setValue, ...props }) => {
  return (
    <>
      {options.map((option) => (
        <label key={option}>
          {/* A reatividado do 'radio' está relacioanda ao checked */}
          <input
            type="radio"
            value={option} // option são  as opções de cores passadas
            checked={value === option} // verifica se o value 
            onChange={({ target }) => setValue(target.value)}
            />
            {console.log(option + '     opção marcada: '+ value )}
          {option}
        </label>
      ))}
    </>
  );
};

export default Radio;
