import React from 'react';
import { useState } from 'react';

const App = () => {
  const [ativo, setAtivo] = useState(true);
  const [dados, setDados] = useState({ id: 1, name: 'Kauã', idade: 19 });


  return (
    <>
      <input
        type="text"
        placeholder="ADD uma faculdade"
        onChange={(e) => setDados({ ...dados, faculdade: e.target.value })} // desestruturando o objeto, passando os valores antigos e adiconando novos
      />

      <button onClick={() => setAtivo(!ativo)}>  {/* setando o valor, passando um valor oposto de 'ativo' */}
        {ativo ? 'Ativo' : 'Inativo'} 
      </button>
      <p>{dados.name}</p>
      <p>{dados.idade}</p>
     {<p>{dados.faculdade}</p>}

    </>
  );
};

export default App;
