import React from 'react';
import Input from './Form/Input';
import useForm from './Hooks/useForm';

export const App = () => {
  const cep = useForm('cep'); // como eu retorno um objeto lá, eu não passo uma array
  const email = useForm('email');
  const nome = useForm();

  // fazendo uma validação ao enviar o formulário
  function handleSubmit(event) {
    event.preventDefault();
    if (cep.validate()) {
      // usando o método do useForm para valdiar
      alert('enviou');
    } else {
      alert('Não enviou');
    }
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <Input
          label="CEP"
          id="cep"
          type="text"
          placeholder="00000-000"
          {...cep} // o que isso fará é espalahr todas essas prorpriedades no useform
          // setValue,
          // error,
          // onChange,
          // onBlur: () => validate(value) // assim que eu utilziar o unBlur ele já fará a validação
        />

        <Input
          label="Email"
          id="email"
          type="email"
          placeholder="exemplo@gmail.com"
          {...email}
        />

        <Input
          label="Email"
          id="email"
          type="email"
          placeholder="exemplo@gmail.com"
          {...email}
        />

        <button>Enviar</button>
      </form>
    </>
  );
};

export default App;
