import React from 'react';

const App = () => {
  const ativo = true;
  function mostraNome(sobrenome) {
    return 'Kauã' + sobrenome;
  }
  const carro = {
    rodas: 4,
    marca: 'Ford',
  };

  // STYLE
  const estiloP = {
    color: 'blue',
    fontSize: '32px',
  };
  return (
    <>
      {' '}
      {/* Sendo apenas uma tag para envolver o contúdo, já que podemos retornar apenas uma coisa */}
      {/* clssName é o "class", porem como o class já é uma palavras reservada, deram o nome de 'Classname' */}
      <a className="ativo" href="https://origamid.com" title="Isso é um site">
        {' '}
        Origamid{' '}
      </a>
      <label htmlFor="nome">Nome</label>
      <input type="text" id="nome"></input>
      {mostraNome('Lusvarghi')}
      <p style={estiloP} className={ativo ? 'ativo' : 'inativo'}>
        Está ativo?
      </p>
      <p style={{ color: 'red', fontFamily: 'Helvetica' }}>
        {new Date().getFullYear()}
      </p>
      <p>{carro.marca}</p> {/* não podendo passar apenas o objeto direto */}
      <p>{Math.random() * 100}</p>
    </>
  );
};

export default App;
