import React from 'react';

// Organize os produtos como mostrado no vídeo
// Mostre apenas produtos que forem mais caros que R$ 1500
const produtos = [
  {
    id: 1,
    nome: 'Smartphone',
    preco: 'R$ 2000',
    cores: ['#29d8d5', '#252a34', '#fc3766'],
  },
  {
    id: 2,
    nome: 'Notebook',
    preco: 'R$ 3000',
    cores: ['#ffd045', '#d4394b', '#f37c59'],
  },
  {
    id: 3,
    nome: 'Tablet',
    preco: 'R$ 1500',
    cores: ['#365069', '#47c1c8', '#f95786'],
  },
];

const App = () => {
  const dados = produtos.filter(({preco}) => (
    Number(preco.replace("R$ ", '')) > 1500
  ))
  console.log(dados)

  return (
    <section>

      {dados.map((item) => ( 
        <div key={item.id}> 
          <h1>{item.nome}</h1>
          <p>Preço R$ {item.preco}</p>
          <ul>{item.cores.map( (cor) => ( //fazendo um map para exibir a array de cores
          // tendo que passar uma key para o li tamebm se não dará erro
            <li key={cor} style={{ backgroundColor: cor,color: 'white'}}>{cor}</li> // exibindo uma cor de cada vez
          ))}</ul>
        </div>
      ))}
    </section>);
};

export default App;
