import React from 'react';
import { useState } from 'react';
import Produtos from './Produtos';

const App = () => {
  const [dados, setDados] = useState(null)
  const [loadding, setLoadding] = useState(null)

  async function handleClick(e) {
    setLoadding(true)
    const response = await fetch(`https://ranekapi.origamid.dev/json/api/produto/${e.target.innerText}`)
    const responseJson = await response.json()
    setDados(responseJson)
    setLoadding(false)

  }

  return (
    <>
      <button style={{ margin: '.5rem' }} onClick={handleClick}>
        tablet
      </button>
      <button style={{ margin: '.5rem' }} onClick={handleClick}>
        smartphone
      </button>
      <button style={{ margin: '.5rem' }} onClick={handleClick}>
        notebook
      </button>
      {loadding && <p>Carregando...</p> }
      {!loadding && dados && <Produtos dados={dados}/>}{/* Assim verificando se dados é difente de null para que não renderize 'produtos antes de clicarem em algum botão */}

    </>
  );
};

export default App;

// Os links abaixo puxam dados de um produto em formato JSON
