import React from 'react';

const useFetch = () => {
  const [data, setData] = React.useState(null); // estado para os dados
  const [error, setError] = React.useState(null); // estado para os errors
  const [loading, setLoading] = React.useState(null); // estado para o loadding

  // fazedno um função de request, para idenpende do momento, oa clicar em um botão, quando o componente montar ele conseguir acessar independente do momento
  async function request(url, options) {
    try{ // para verificar  o erro, utilizamos o try e catch
      setError(null) // ao renderizar passamos 'null' para que nãoq fique exibindo o erro direto na tela mesmo que o usuário tente novamente fazer esse request
      setLoading(true) // ao iniciar o fetch ele irá aparecer carreganod na tela
      // passando a url e as opções, para quando se quer fazer um POST, GET, DELETE, etc
      const response = await fetch(url, options);
      const json = await response.json();
      setData(json);
    } catch(error){ // caso de erro, ele exibe o que está dentro do catch
      setError(error)
    }finally{ // e sempre irá executar o finally independente de tudo
      // ao finalizar o loading ele aparece false
      setLoading(false) // setando o valor para 'false' por caonta que no try ele set o valor para 'true' no setLoading e em caso de erro ele não chega a setar o valor para 'false', então de qualuer maneira temos que fazer isso no finally
      return
    }
  }

  return { data, error, loading, request }; // retornando como objeto por conta que quando eu for utilizar eles, eu terei que desestruturar na ordem correta, já quando eu eretorno como objeto eu só coloco o nome
};

export default useFetch;
