import React from 'react';

const Radio = ({ pergunta, options, id, onChange, value }) => {
  return (
    <fieldset
      style={{
        padding: '2rem',
        marginBottom: '1rem',
        border: '2px solid #eee',
      }}
    >
      {' '}
      {/* fildset serve para agrupar campos de formulário */}
      <legend style={{ fontWeight: 'bold' }}>{pergunta}</legend>
      {options.map((opcao) => (
        <label
          key={id}
          style={{ marginBottom: '1rem', fontFamily: 'monospace' }}
        >
          <input
            type="checkbox"
            value={opcao}
            name={id}
            id={id}
            onChange={onChange}
            checked={value}
          />
          {opcao}
        </label>
      ))}
    </fieldset>
  );
};

export default Radio;
