import React from 'react';
import { useEffect, useState } from 'react';
import Produto from './Produto';

const App = () => {
  const [produto, setProduto] = useState(undefined)

  async function handleClick(e) {
    // console.log(e.target.innerHTML)
    const response = await fetch(`https://ranekapi.origamid.dev/json/api/produto/${e.target.innerHTML}`)
    const json = await response.json()
    setProduto(json)
    console.log(produto)
  }

  return (
    <>
    {<h1>Preferência: {produto.nome}</h1>}
      <button style={{marginRight: '2rem'}} onClick={handleClick}>notebook</button>
      <button onClick={handleClick}>smartphone</button>
      <h1>{produto.nome}</h1>
      <p>R$ {produto.preco}</p>
    </>
  );
};

export default App;

// Quando o usuário clicar em um dos botões, faça um fetch do produto clicado utilizando a api abaixo
