import React from 'react';

const formFields = [
  {
    id: 'nome',
    label: ''
  }
]

const App = () => {
  // nome, email, senha, cep, rua, numero, bairro, cidade e estado
  const [form, setForm] = React.useState({
    nome: '',
    email: '',
    senha: '',
    cep: '',
    rua: '',
    numero: '',
    bairro: '',
    cidade: '',
    estado: '',
  });

  function handleSubmit(e) {
    e.preventDefault(); // previnindo o evento padrão para que ele não atualize a página
  }

  function handleChange({ target }) {
    // desestruturando o 'event' e pegando apenas o que irei utilizar que é o 'target
    const { id, value } = target; // desestruturando o target e pegando o 'id' e o 'value'
    console.log(id, value);

    setForm({ ...form, [id]: value }); // passanod priemiro os valores antigos para ele não passar por cima
    // [id]: value ===> essa parte é a mesma coisa que ["email"]: kaua@gmail.com
    // caso não passe o 'id' entre [], ele não irá torar um itme reativo, ai só com os colchetes ele tranforma a variavel "id" como propriedade
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label htmlFor="nome">Nome</label>
        <input
          id="nome"
          name="nome"
          type="text"
          value={form.nome}
          onChange={handleChange}
        />
        <label htmlFor="email">Email</label>
        <input
          id="email"
          name="email"
          type="email"
          value={form.email}
          onChange={handleChange}
        />
        <label htmlFor="password">Senha</label>
        <input
          id="password"
          name="password"
          type="password"
          value={form.senha}
          onChange={handleChange}
        />

        <label htmlFor="cep">CEP</label>
        <input
          id="cep"
          name="cep"
          type="text"
          value={form.cep}
          onChange={handleChange}
        />
        <label htmlFor="rua">Rua</label>
        <input
          id="rua"
          name="rua"
          type="text"
          value={form.rua}
          onChange={handleChange}
        />
        <label htmlFor="numero">Numero</label>
        <input
          id="numero"
          name="numero"
          type="text"
          value={form.numero}
          onChange={handleChange}
        />
        <label htmlFor="bairro">Bairro</label>
        <input
          id="bairro"
          name="bairro"
          type="text"
          value={form.bairro}
          onChange={handleChange}
        />
         <label htmlFor="cidade">Cidade</label>
        <input
          id="cidade"
          name="cidade"
          type="text"
          value={form.cidade}
          onChange={handleChange}
        />
         <label htmlFor="estado">Estado</label>
        <input
          id="estado"
          name="estado"
          type="text"
          value={form.estado}
          onChange={handleChange}
        />
        <button>Enviar</button>
      </form>
      {/* {} */}
    </>
  );
};

export default App;

