import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './Home';
import Sobre from './Sobre';
// BrowserRouter é o componente que irá envolver todo o seu aplicativo que irá depender de alguma coisa relacioanda a 'Router'
// Routes - dentro dele envolvemos apenas as rotas especificas (Route), colcoando comonentes fixos fora dele, como o header e footer do site
// Route - sendo o caminho em si

const App = () => {
  return (
    <BrowserRouter>
      <Routes>
        <Route path='/' element={<Home />}/> {/* sendo passado primeiro a rota, "/" significa que ele irá para a Home; e o element é o componente a ser renderizado */}
        <Route path='Sobre' element={<Sobre />}/>

      </Routes>
    </BrowserRouter>
  );
};

export default App;
