import Input from './Form/Input';

export const App = () => {
  const [cep, setCep] = React.useState('');
  const [error, setError] = React.useState(null)

  function validateCep(value){ // recebendo com oparemetro o valor digitado pelo usuário
    console.log(value)
    if(value.length === 0){
      setError('Preencha um valor!')
      return false
    }else if(/^\d{5}-?\d{3}$/.test(value)){ 
      // verifica se o valor digitado é válido
      // aqui ele verifica se há 5 digitos, depois verifica se tem '-', porem sendo opcioanl porconta do ponto de "?" que vem em sua sequencia, e por ultimo verifica se há mais 3 digitos em sequencia
    // porem precismaos dizer que ele começa com 5 digitos utiizando o "^" e para dizer que termina com 3 digitos usamos "$"
      setError('Preencha um valor válido!')
      return false
    } else {
      setError(null)
      return true
    }
  }

  function handleBlur({target}){
    validateCep(target.value)


