const App = () => {
  const [select, setSelect] = React.useState('');
  return (
    <>
      <form action="">
        <select
          value={select}
          onChange={({ target }) => setSelect(target.value)}
          id="produtos"
        >
          <option disabled value="">
            Selecione
          </option> 
          {/*  */}
