import React from 'react'

const Input = ({label, id}) => { // desestruturando a label, querendo então passar o nome dela
  return (
    <div syyle={{margin:'1rem 0'}}>
      <label htmlFor="{id}">{label}</label>
      <input id="{id}" type="text"/>
    </div>
  )
}

export default Input
