import React from 'react'
import { useNavigate } from 'react-router-dom'

const Login = () => {
  const navigateToAccount = useNavigate() // sendo ele um Hook

  // em um caso real, ao clicar no botão ele faria um fetch ao servidor e confirmaria as credenciais do usuário e só depois de tudo certo ele excecutaria a linha onde tem o 'navigateToAccount' e jogaria para a conta do usuário co mas informações dele
  function handleClick(){
    console.log('Fazer login')
    navigateToAccount('/sobre') // tendo que passar o caminho completo com a "/" desde de o ddiretório raiz
  }

  return (
    <div>
      <h1>Login</h1>
      <button onClick={handleClick}>Fazer Login</button>
    </div>
  )
}

export default Login
