import React from 'react';

const Radio = ({ options }) => {
  return (
    <>
      {options.map((option) => (
        <label key={option}>
          {/* A reatividado do 'radio' está relacioanda ao checed */}
          <input type="radio" value={option} />
          {option}
        </label>
      ))}
    </>
  );
};

export default Radio;
