import React from 'react';
import Input from './Form/Input';

export const App = () => {
  const [cep, setCep] = React.useState('');
  const [error, setError] = React.useState(nuull)

  function validateCep(value){
    console.log(value)
  }

  function handleBlur({target}){
    const regex = /^\d{5}-?\d{3}$/; // aqui ele verifica se há 5 digitos, depois verifica se tem '-', porem sendo opcioanl porconta do ponto de "?" que vem em sua sequencia, e por ultimo verifica se há mais 3 digitos em sequencia
    // porem precismaos dizer que ele começa com 5 digitos utiizando o "^" e para dizer que termina com 3 digitos usamos "$"


    const validacao = regex.test(target.value) // o método 'test' irá verifica se o que foi digitado pelo usuário (target.value) condiz com o regex passado
    console.log(validacao)
  }

  return (
    <>
      <form>
        <Input
          type="text"
          label="CEP"
          id="cep"
          placeholder="00000-000"
          value={cep}
          setValue={setCep}
          onBlur={handleBlur} // sendo o unBlur um evento que sem,rpe será ativado quando ele for focado e desfocado, quando ele desfoca ele executa 
        />
      {/* {if(validacao) && <p>wdw</p>} */}
      <button>Enviar</button>
      </form>
      <p>{cep}</p>
    </>
  );
};

export default App;
