import React from 'react';
import { useEffect, useState } from 'react';
import Produto from './Produto';

const App = () => {
  const [produto, setProduto] = useState(null)

  useEffect(() => { // executando apenas ujma vez
    const produtoPreferencia = window.localStorage.getItem('produto') // passando o valor que está salvo no localstorage, passando apenas a "key", que é 'produto'
    if(produtoPreferencia !== null) setProduto(produtoPreferencia) // caso tenha algum poduto salvo no localStorage, ele irá passar o valor salvo que está na variavel 'produtoPreferencia' para o 'produto'
    console.log(produtoPreferencia)
  }, [])

  useEffect(() => { // assim executando apenas quando  'produto' sofre alguma alteração
    if(produto !== null){ // por padrão o useEfect é renderizado uma vez, e para que não seja salvo no localstorage se não tiver nenhum valor em 'produto, apenas verificamos seu valor
      window.localStorage.setItem('produto', produto) // salvando dentro dp localStorage
      // sendo o primeiro parâmentro a chave (key), e o segundo o valor (value)
    }
  }, [produto])

  function handleClick(e) {
    setProduto(e.target.innerText)
  }


  return (
    <>
    { produto && <h1>Preferência: {produto}</h1>}
      <button style={{marginRight: '2rem'}} onClick={handleClick}>notebook</button>
      <button onClick={handleClick}>smartphone</button>
      <Produto produto={produto}/> {/* mandando o useState produto como parâmetro  */}
      {/* <h1>{produto.nome}</h1> */}
      {/* <p>R$ {produto.preco}</p> */}
    </>
  );
};

export default App;

// Quando o usuário clicar em um dos botões, faça um fetch do produto clicado utilizando a api abaixo
// https://ranekapi.origamid.dev/json/api/produto/notebook
// https://ranekapi.origamid.dev/json/api/produto/smartphone
// Mostre o nome e preço na tela (separe essa informação em um componente Produto.js)
// Defina o produto clicado como uma preferência do usuário no localStorage
// Quando o usuário entrar no site, se existe um produto no localStorage, faça o fetch do mesmo
