import React from 'react';
import useFetch from './useFetch';
import useLocalStorage from './useLocalStorage';

const App = () => {
  const [produto, setProduto] = useLocalStorage('produto', '');
  const { request, data, loading, error } = useFetch(); // como eu estou retornando um objeto lá, eu posso desestruturar ele aqui
  // dentro do data eu tenho acesso ao 'json', porem não tenho acesso ao a variavel 'response' definida no useEffect, snedo ela important em alguns momento para mim, podendo retornar ela no finally para mim

  React.useEffect(() => {
    // como o request é uma função assincrona (feita lá em useFetch), para que tenhamos acesso a ela temos que trnsforma a função em asincrona
    async function fetchData() {
      // tendo assim o 'response' e o 'json' que foram retornando no 'finally'
      const {response, json} = await request('https://ranekapi.origamid.dev/json/api/produto/'); // desestruturnado o obj, passando já os nome a serem utilizado
      console.log(response, json) // sendo o 'response' dados de status, etc, e o json a array de produtos, se eu não desestruturasse eu teria que utilizar obj.response
    }
    fetchData()
  }, []); // e para solucionar o erro que dava por falta de uma dependecia 
  // console.log(data) // me retornando a array de objetos contendo os produtos

  function handleClick({ target }) {
    // desestruturando o 'event'
    return setProduto(target.innerText);
  }
  if (error) return <p>Houve um error: {error}</p>;
  if (loading) return <p>Carregando...</p>;
  if (data)
    // antes de exibir os produtos, tempos que verificar se 'data' é true e evitar problemas
    return (
      <div>
        <p>Produto preferido: {produto}</p>
        <button style={{ marginRight: '1rem' }} onClick={handleClick}>
          notebook
        </button>
        <button onClick={handleClick}>smartphone</button>

        {data.map((produto) => (
          <div key={produto.id}>
            <h1>{produto.nome}</h1>
          </div>
        ))}
      </div>
    );
  else return null; // e caso nenhuma das consições sejá verdadeira ele retorna null
};

export default App;
