import React from 'react';
import useFetch from './useFetch';
import useLocalStorage from './useLocalStorage';

const App = () => {
  const [produto, setProduto] = useLocalStorage('produto', '');
  const { request, data, loading, error } = useFetch(); // como eu estou retornando um objeto lá, eu posso desestruturar ele aqui

  React.useEffect(() => {
    request('https://ranekapi.origamid.dev/json/api/produto/');
  }, []);
  // console.log(data) // me retornando a array de objetos contendo os produtos

  function handleClick({ target }) {
    // desestruturando o 'event'
    return setProduto(target.innerText);
  }
  if(error) return 
  if (loading) return <p>Carregando...</p>;
  if (data)
    // antes de exibir os produtos, tempos que verificar se 'data' é true e evitar problemas
    return (
      <div>
        <p>Produto preferido: {produto}</p>
        <button style={{ marginRight: '1rem' }} onClick={handleClick}>
          notebook
        </button>
        <button onClick={handleClick}>smartphone</button>

        {data.map((produto) => (
          <div key={produto.id}>
            <h1>{produto.nome}</h1>
          </div>
        ))}
      </div>
    );
  else return null; // e caso nenhuma das consições sejá verdadeira ele retorna null
};

export default App;
