import React from 'react';
import { useEffect, useState } from 'react';
import Produto from './Produto';

const App = () => {
  const [ativo, setAtivo] = useState(false);

  return (
    <div>
      {ativo && <Produto />}
      <button onClick={() => !ativo}>Ativo</button>
    </div>
  );
};

export default App;
