import React from 'react';
import Radio from './Form/Radio';

const perguntas = [
  {
    pergunta: 'Qual método é utilizado para criar componentes?',
    options: [
      'React.makeComponent()',
      'React.createComponent()',
      'React.createElement()',
    ],
    resposta: 'React.createElement()',
    id: 'p1',
  },
  {
    pergunta: 'Como importamos um componente externo?',
    options: [
      'import Component from "./Component"',
      'require("./Component")',
      'import "./Component"',
    ],
    resposta: 'import Component from "./Component"',
    id: 'p2',
  },
  {
    pergunta: 'Qual hook não é nativo?',
    options: ['useEffect()', 'useFetch()', 'useCallback()'],
    resposta: 'useFetch()',
    id: 'p3',
  },
  {
    pergunta: 'Qual palavra deve ser utilizada para criarmos um hook?',
    options: ['set', 'get', 'use'],
    resposta: 'use',
    id: 'p4',
  },
];

export const App = () => {
  const [respostas, setRespostas] = React.useState({
    p1: '',
    p2: '',
    p3: '',
    p4: '',
  });
  const [slide, setSlide] = React.useState(0) // assim o slide começa com 0 (1º item)

  // funça~resposnsavel por atribuir a resposta do usuário a cada pergunta, p1, p2, p3 e p4
  function handleChange({ target }) {
    // console.log(target.value) // me retorna o valor clicado
    // console.log(target.id) // retorna o id da pergunta
    // é a mesma cosia que p1: valorClicado
    setRespostas({...respostas, [target.id]: target.value }) // a primeria coisa a se fazer quando modifica um objeto é desestrutura os valores dele e passar o novo valor
    console.log(respostas) // me mostra o objeto de respostas contendo as respostas clicadas
  }

  // Função responsalvel por mudar o estado do slide e mudar para a próxima página
  function handleClick(e) {

  }

  return (
    <form>
      {perguntas.map((question, index) => ( // o map permite passar duas prorpiedades até, a priemira é o valor de cada laço e o segundo é o index
        <Radio
          active={slide === index} // sendo ele responsavel por passar true ou false para que seja mostrado na tela
          key={question.id}
          id={perguntas.id}
          value={respostas[question.id]} // no value precisamos passar o nosso Reativo (respostas/useRespostas), porem precisamos passar a responsta conforme a questão correta, sendo no 'question.id' quem tem a posição de cada pergutna
          onChange={handleChange}
          {...question}
        />
      ))}
      {/* Passamndo todas as prorpiedades de perguntas para o radio */}
      <button onClick={handleClick}>Proxima</button>
    </form>
  );
};

export default App;
