const Input = ({ id, label, onChage, value, setValue, type, onBlur, placeholder, error }) => { // desestruturand tudo que irei utiliar invez de espalhar as props, par aque não de um erro e ele espalhe o setValue indevidaemnte
  
  return (
    <>
      <label htmlFor={id}>{label}</label>
      <input
        type="text"
        name={id}
        id={id}
        onChange={onChage} // neste caso expecifico do CEP, temo que fazer uma verificação após o usuário ter mudado campo depois da priemira vez
        placeholder={placeholder}
      />
    </>
  );
};

export default Input;
