import React from 'react';

const App = () => {
  const [car, setCar] = React.useState(0);
  const [notification, setNotification] = React.useState(null);
  const timeRef = React.useRef();


  function handleClick() {  
    setCar(car + 1)
    setNotification('Item adicionado ao Carrinho')


    // toda vez que eu ativar a função, ele primeiro irá limpar o tmeout anterior
    // logo após irpa

    clearTimeout(timeRef.current)
    timeRef.current = setTimeout(() => {
      setNotification(null)
    }, 2000)
  }

  return (
    <div>
      <p>{notification}</p>
      <button onClick={handleClick}>Adicioanr ao Carrinho {car}</button>
    </div>
  );
};

export default App;
