import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';

const Header = () => {

  // Como o header é um elemento global no meu site (ele aparece em todas as abas), é uma boa forma de aplica um efeito ao mudar de página
  const location = useLocation() // declarando o useLocation e passando a uma variavel
  // Podendo usar o 'Location' para toda vez que eu mudar de rota ele ativa um efeito
  React.useEffect(() => {
    console.log('Mudou de rota')
    console.log(location)
  }, [location]) // passando a rota como caminho

  return (
    <nav>
      {/* Para que não tenha que importar estilização, ainda mais coisas pequenas, podemos aplicar o 'activeStyle' que irá estilizar o 'navLink' que estiver com a classe 'active' */}
      <NavLink to="/" end  activeStyle={{color: 'tomato'}}>Home</NavLink> | 
      <NavLink to="sobre" activeStyle={{color: 'tomato'}}>Sobre</NavLink> |
      <NavLink to="login" activeStyle={{color: 'tomato'}}>Login</NavLink> | 
      <NavLink to="produto" activeStyle={{color: 'tomato'}}>Produto</NavLink>

    </nav>
  );
};

export default Header;
