import React from 'react';
import { useState } from 'react';

const App = () => {
  const [ativo, setAtivo] = useState(true);
  return (
    <>
      <input type="text" name="" id="" placeholder='qual faculdade deseja adiconar ao o'/>
      <button onClick={(e) => setAtivo(!ativo)}>Adicionar</button>
      <button onClick={(e) => setAtivo(!ativo)}>{ativo ? 'Ativo' : 'Inativo'}</button>
    </>
  );
};

export default App;
