import React from 'react';
import CheckBox from './Form/CheckBox';
import Input from './Form/Input';
import Radio from './Form/Radio';
import Select from './Form/Select';

const App = () => {
  // Utiizando um estado para cada item para que na hora da validação fique mais simples de se tratar
  const [nome, setNome] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [produto, setProduto] = React.useState('');
  const [cor, setCor] = React.useState('Vermelho');
  const [frutas, setFrutas] = React.useState('Banana');
  const [lingaguem, setLingaguem] = React.useState([]);
  const [termos, setTermos] = React.useState([]);

  if (termos.length > 0) alert('Enviar');

  return (
    <>
      <form>
        <h2>Termos </h2>
        <CheckBox
          options={['Li e aceito os termos e condições']}
          value={termos}
          setValue={setTermos}
        />

        <h2>Linguagens de Programação </h2>
        <CheckBox
          options={['JavaScript', 'PHP', 'JAVA', 'C++', 'Ruby']}
          value={lingaguem}
          setValue={setLingaguem}
        />

        <h2>Cores</h2>
        {/* Mesmo não se utilizando 'options' em inputs do tipo 'Radio', podemos utilizar o nome 'options', assim fica mais organizado nosso código */}
        <Radio options={['Azul', 'Vermelho']} value={cor} setValue={setCor} />

        <h2>Frutas</h2>
        <Radio
          options={['Banana', 'Maça', 'Pera']}
          value={frutas}
          setValue={setFrutas}
        />

        <h2>Produtos</h2>
        <Select
          options={['Smartphone', 'Notebook']}
          value={produto}
          setValue={setProduto}
        />

        <h2>Formulário</h2>
        <Input id="nome" label="Nome" value={nome} setValue={setNome} />
        <Input
          id="email"
          label="Email"
          value={email}
          setValue={setEmail}
          required
        />

        <button>Enviar</button>
      </form>
    </>
  );
};

export default App;
