import React from 'react';
import { useState } from 'react';

const App = () => {
  const [contar, setContar] = useState(0);
  const [item, setItem] = useState(['Item 0']);

  function handleClick() {
    setContar((prev) => prev + 1); // não precisando abrir a função anonima se o retorno dela for apenas uma linha
    setItem((item) => [...item, 'Item' + (contar + 1)]); // 
  }

  return (
    <div>
      <button onClick={handleClick}>{contar}</button>
      {item.map( (item) => (
        <li>{item}</li>
      ))}
    </div>
  );
};

export default App;
