const App = () => {
  const [box, setBox] = React.useState(false);

  return (
    <>
      <form>
        {box && <p>Aceitou os termos e condições</p>}
        {/* verifica se é true */}
        <label>
          <input
            type="checkbox"
            name=""
            value="Termos"
            checked={box}
            onChange={({ target }) => setBox(target.checked)} // Sendo a prorpiedade 'target.checked' me retornar true ou false,
          />{' '}
          Aceito os termos
        </label>
      </form>
    </>
  );
};

export default App;
