import React from 'react';

// Organize os produtos como mostrado no vídeo
// Mostre apenas produtos que forem mais caros que R$ 1500
const produtos = [
  {
    id: 1,
    nome: 'Smartphone',
    preco: 'R$ 2000',
    cores: ['#29d8d5', '#252a34', '#fc3766'],
  },
  {
    id: 2,
    nome: 'Notebook',
    preco: 'R$ 3000',
    cores: ['#ffd045', '#d4394b', '#f37c59'],
  },
  {
    id: 3,
    nome: 'Tablet',
    preco: 'R$ 1500',
    cores: ['#365069', '#47c1c8', '#f95786'],
  },
];

const App = () => {
  const dados = produtos.filter(({preco}) => (
    Number(preco.replace("R$ ", '')) > 1500
  ))
  console.log(dados)

  return (
    <section>
      {/* Para exibir mais de um valor e forma automatica, sem ter que replicar código, fazemos uma nova array para exibir */}
      {dados.map((item) => ( // desestruturando o Objeto para simplificar a leitura
        <div key={item.id}> {/* tendo que passar o id */}
          <h1>{item.nome}</h1>
          <p>Preço R$ {item.preco}</p>

        </div>
      ))}
    </section>);
};

export default App;
