import React from 'react';
import styles from './Slide.module.css';

const Slide = ({ slides }) => {
  const [active, setActive] = React.useState(0) // Reac=tivo respponsavel pelo item ativo na tela
  const [position, setPositon] = React.useState(0)
  const contentRef = React.useRef() // hoock que terá uma referencia da div 'content'

  function slidePrev() {

  }

  function slideNext() {
    // pegando a posição inicial dele e tendo que pegar o tamanho do elemento, e não da tela
    // para ter acesso direto ao elemento tem que passar a referencia (contentRef) junto com o .current (contentRef.current) 
    // e para pegar valores de posição desse elemento, peganto tanto a posição dele quanto o tamanho dele na tela 'contentRef.current.getBoundingClientRect()'
    const {width} = contentRef.current.getBoundingClientRect() // desestruturando o objeto que ele retorna e pegando apenas o width que me interessa, sendo a largura dele
    setPositon(position - width)
    console.log(position)
  }

  // para eu pegar o tamanho da tela eu posso usar o useRef e ter o valor da div 'content'

  return (
    <section className={styles.container}>
      {/* O valor da transição é um numero que muda e que será feita baseando-se pelo tamanho da tela, então terá que ser ums useState */}
      <div 
      ref={contentRef} // assim tendo tendo referencia ao 'conten' com a variavel 'contentRef' 
      className={styles.content} 
      style={{transform: `translateX(${position}px)`}}>
        {slides.map(({ id, text }) => (
          <div key={id} className={styles.item}>
            {text}
          </div>
        ))}
      </div>
      <nav className={styles.nav}>
        <button onClick={slidePrev}>Anterior</button>
        <button onClick={slideNext}>Próximo</button>
      </nav>
    </section>
  );
};

export default Slide;
