import React from 'react';
import { useEffect } from 'react';
import Produtos from './Produtos';

const App = () => {
  const [dados, setDados] = useState(null)
  const [loadding, setLoadding] = useState(null)
  useEffect(() => {
    console.log('Será exibido apenas uma vez quando o site for renderizado') 
   })
  async function handleClick(e) {
    setLoadding(true) // ao clicar ele irá dar como true, para que exiba a mensagem de 'loadding' lá em baixo
    const response = await fetch(`https://ranekapi.origamid.dev/json/api/produto/${e.target.innerText}`) // já que todas as API's são iguais, buscando apenas o falor escrito no 'button' e colcoando no final da API, já faz com que não sejá escrito varias vezes coisas similares
    const responseJson = await response.json()
    setDados(responseJson)
    setLoadding(false) // após tudo carregado ele tira a mensagem
  }

  return (
    <>
      <button style={{ margin: '.5rem' }} onClick={handleClick}>
        tablet
      </button>
      <button style={{ margin: '.5rem' }} onClick={handleClick}>
        smartphone
      </button>
      <button style={{ margin: '.5rem' }} onClick={handleClick}>
        notebook
      </button>
      {loadding && <p>Loadding...</p> } {/* caso o 'loaddin' seja true ele exibe e mensagem */}
      {!loadding && dados && <Produtos dados={dados}/>}{/* Assim verificando se o loadding é 'falso' e se dados é difente de null para que não renderize 'produtos antes de clicarem em algum botão */}
    </>
  );
};

export default App;

// Os links abaixo puxam dados de um produto em formato JSON
// https://ranekapi.origamid.dev/json/api/produto/tablet
// https://ranekapi.origamid.dev/json/api/produto/smartphone
// https://ranekapi.origamid.dev/json/api/produto/notebook
// Crie uma interface com 3 botões, um para cada produto.
// Ao clicar no botão faça um fetch a api e mostre os dados do produto na tela.
// Mostre apenas um produto por vez
// Mostre a mensagem carregando... enquanto o fetch é realizado
