import React from 'react';
import styles from './Slide.module.css';

const Slide = ({ slides }) => {
  return (
    <section className={styles.container}>
      <div className={styles.content}>
        {slides.map(({ id, text }) => (
          <div key={id} className={styles.content}>
            {text}
          </div>
        ))}
      </div>
    </section>
  );
};

export default Slide;
