import React from 'react';

const Head = (props) => {
  React.useEffect(() => {
    document.title = "React | " + props.title
    document.querySelector('meta[name="description"]').setAttribute('content', props.description) // selecionando o atributo que quero mudar, usando o "setAttribute" para modificar / setar um novo nome, passando o atributo que quero mudar, e depois como segundo parametro o valor que ele irá receber
    console.log(props)
  }, [props]);
  
  return <></>;
};

export default Head;
