import React from 'react';

const App = () => {
  const [cores, setCores] = React.useState(['azul']);

  function handleChange({ target }) {
    // console.log(target.value) // sendo os valores marcados e desmacados ao clicar
    if (target.checked) {
      // antes ele dava um bug que quando desmarcavamos o checkboox ele adicionava o valor a array, com essa verificação isso parou, porem ainda não removeu o item da array
      setCores([...cores, target.value]); // passando a array, depois a array dos valores anteriores + o valor atual
    } else {
      // e para remover o valor desmarcado
      // então eu quero pegar uma array, retornar ela sem um valor especifico, que é o 'target.value'
      setCores(
        cores.filter((callback) => {
          // Passando um callback como retorno
          console.log(callback); // sempre que eu desmarco um item ele aciona esse console.log com a cor desmarcada
          return callback !== target.value; // se ele retornar isso quer dizer que 'callbak' não é a checada
          // nessa logica, ele retorna no 'return' a variavel 'callback' (que é a opção não checada) é diferente de 'target.value' que é a opção checada, assim removendo da array os valores não checados
        }),
      );
    }
  }

  function checkedColor(cor){ //recebendo a cor como parametro
    return cores.includes(cor) // tambem retornando true or false
  }

  return (
    <>
      <form>
        <label>
          <input
            type="checkbox"
            value="azul"
            checked={checkedColor('azul')} //ou podendo fazer em forma de função tambem
            onChange={handleChange}
          />{' '}
          Azul
        </label>
        <label>
          <input
            type="checkbox"
            value="vermehlo"
            checked={cores.includes('vermelho')} // não podendo deixar o Checked sem controle, caso o usuário tenha preferencias. Podendo verificar com o includes, que retornar true ou false, que verifica se o parametro passado tem dentro da variavel 'cores'
            onChange={handleChange}
          />{' '}
          Vermelho
        </label>
      </form>
    </>
  );
};

export default App;
